import React, { useState } from 'react';
import { AppBar, Box, CssBaseline, Drawer, List, ListItem, ListItemText, Toolbar, Typography, IconButton, Button, Divider, useMediaQuery } from '@mui/material';
// import { Menu as MenuIcon } from '@mui/icons-material';

function DashboardLayout() {
  const [open, setOpen] = useState(false); // State to toggle sidebar open/close


  const toggleDrawer = () => setOpen(!open);
  const drawerWidth = 240;

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />

      {/* Header (AppBar) */}
      <AppBar position="sticky">
        <Toolbar>
          {/* Menu Icon (for mobile sidebar toggle) */}
          <IconButton
            color="inherit"
            edge="start"
            aria-label="menu"
            sx={{ mr: 2, ...(isMobile && { display: 'block' }) }}
            onClick={toggleDrawer}
          >
            {/* <MenuIcon /> */}
          </IconButton>

          {/* Title */}
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Dashboard
          </Typography>

          {/* Action buttons (e.g., logout, profile) */}
          <Button color="inherit">Profile</Button>
          <Button color="inherit">Logout</Button>
        </Toolbar>
      </AppBar>

      {/* Sidebar (Drawer) */}
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant={isMobile ? 'temporary' : 'persistent'} // Make drawer temporary on mobile
        anchor="left"
        open={open}
        onClose={toggleDrawer}
        ModalProps={{
          keepMounted: true, // Better performance on mobile
        }}
      >
        {/* Sidebar content */}
        <List>
          <ListItem>
            <ListItemText primary="Dashboard" />
          </ListItem>
          <Divider />
          <ListItem>
            <ListItemText primary="Profile" />
          </ListItem>
          <ListItem>
            <ListItemText primary="Settings" />
          </ListItem>
          <ListItem>
            <ListItemText primary="Reports" />
          </ListItem>
          <Divider />
          <ListItem>
            <ListItemText primary="Logout" />
          </ListItem>
        </List>
      </Drawer>

      {/* Main Content */}
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          bgcolor: 'background.default',
          p: 3,
          marginLeft: `${open ? drawerWidth : 0}px`, // Adjust main content when sidebar is open
          transition: 'margin-left 0.3s', // Smooth transition when opening/closing the drawer
        }}
      >
        <Toolbar />
        <Typography variant="h4">Welcome to the Dashboard!</Typography>
        <Typography variant="body1" sx={{ mt: 2 }}>
          This is your main content area.
        </Typography>
      </Box>
    </Box>
  );
}

export default DashboardLayout;
