import React, { useState } from 'react';
import {
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Collapse,
  Divider,
  IconButton,
} from '@mui/material';
import { Dashboard, Settings, ExpandMore, ExpandLess, AccountCircle, Inbox } from '@mui/icons-material';

const Sidebar = ({ open, onClose }) => {
  const [openSettings, setOpenSettings] = useState(false);
  const [openAccount, setOpenAccount] = useState(false);

  const handleToggleSettings = () => {
    setOpenSettings(!openSettings);
  };

  const handleToggleAccount = () => {
    setOpenAccount(!openAccount);
  };

  return (
    <Drawer
      sx={{
        width: 240,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: 240,
          boxSizing: 'border-box',
        },
      }}
      anchor="left"
      open={open}
      onClose={onClose}
    >
      <List>
        {/* Dashboard Item */}
        <ListItem button>
          <ListItemIcon>
            <Dashboard />
          </ListItemIcon>
          <ListItemText primary="Dashboard" />
        </ListItem>

        {/* Settings Menu with Submenu */}
        <ListItem button onClick={handleToggleSettings}>
          <ListItemIcon>
            <Settings />
          </ListItemIcon>
          <ListItemText primary="Settings" />
          {openSettings ? <ExpandLess /> : <ExpandMore />}
        </ListItem>
        <Collapse in={openSettings} timeout="auto" unmountOnExit>
          <List component="div" disablePadding>
            <ListItem button sx={{ pl: 4 }}>
              <ListItemText primary="General" />
            </ListItem>
            <ListItem button sx={{ pl: 4 }}>
              <ListItemText primary="Security" />
            </ListItem>
          </List>
        </Collapse>

        {/* Account Menu with Submenu */}
        <ListItem button onClick={handleToggleAccount}>
          <ListItemIcon>
            <AccountCircle />
          </ListItemIcon>
          <ListItemText primary="Account" />
          {openAccount ? <ExpandLess /> : <ExpandMore />}
        </ListItem>
        <Collapse in={openAccount} timeout="auto" unmountOnExit>
          <List component="div" disablePadding>
            <ListItem button sx={{ pl: 4 }}>
              <ListItemText primary="Profile" />
            </ListItem>
            <ListItem button sx={{ pl: 4 }}>
              <ListItemText primary="Notifications" />
            </ListItem>
          </List>
        </Collapse>

        {/* Other Menu Items */}
        <Divider />
        <ListItem button>
          <ListItemIcon>
            <Inbox />
          </ListItemIcon>
          <ListItemText primary="Inbox" />
        </ListItem>
      </List>
    </Drawer>
  );
};

export default Sidebar;
